var s = msg.payload; // samples

// float pitch = (atan2(_ay, sqrt(_ax * _ax + _az * _az))) * 180.0 / M_PI;
// float roll = (atan2(_ax, sqrt(_ay * _ay + _az * _az))) * 180.0 / M_PI;
// return sqrt(pitch * pitch + roll * roll);

var tilts = [];

for ( var i = 0; i < s.length; i++ ) {
    var ax = s [i].a.x;
    var ay = s [i].a.y;
    var az = s [i].a.z;
    //node.warn ( "ax=" + ax + " ay=" + ay + " az=" + az + " t=" + s [i].t );
    var pitch = Math.atan2 ( az, Math.sqrt ( ax*ax + ay*ay ) );
    var roll  = Math.atan2 ( ax, Math.sqrt ( az*az + ay*ay ) );
    var tilt = Math.sqrt ( pitch*pitch + roll*roll );
    //node.warn ( "pitch=" + pitch + " roll=" + roll + " tilt=" + tilt );
    var t = 180.0 / Math.PI * tilt;
    //node.warn ( "tilt=" + t + "°" );
    tilts [tilts.length] = t;
}

var avgTilt = 0;
for ( var i = 0; i < tilts.length; i++ ) {
    avgTilt += tilts [i];
}
avgTilt /= tilts.length;
//node.warn ( "tilt=" + avgTilt + "°" );

msg.payload = avgTilt.toFixed ( 2 );
msg.topic = "Neigung";

return msg;
