// junand 15.01.2017
// convert open weather forecast to pixel node message structure
//
// the payload is the 5 day forecast from open weather, with every 3 hor forecast
// msg.payload [0] -> now
// msg.payload [1] -> +3h
// ...
// msg.payload [40] -> ...

function temperatureMessage ( day, min, max ) {
    return day + " " + min + "°C bis " + max + "°C";
}

function descriptionMessage ( day, description ) {
//    return day + " " + description;
    return day + " " + description.charAt ( 0 ).toLowerCase () + description.slice ( 1 );
}

// calculate min/max temp
var per3h = msg.payload;
var todayDate = per3h [0].dt_txt.split ( " " ) [0];
var tomorrowDate = per3h [8].dt_txt.split ( " " ) [0]; // next day

var todayMinTemp = 999;
var todayMaxTemp = -999;
var tomorrowMinTemp = 999;
var tomorrowMaxTemp = -999;

for ( var i = 0; i < 16; i++ ) { // check only the first two day
    var dt = per3h [i].dt_txt.split ( " " );
    if ( dt [0] === todayDate ) {
        if ( per3h [i].main.temp_min < todayMinTemp ) todayMinTemp = per3h [i].main.temp_min;
        if ( per3h [i].main.temp_max > todayMaxTemp ) todayMaxTemp = per3h [i].main.temp_max;
    }
    else if ( dt [0] === tomorrowDate ) {
        if ( per3h [i].main.temp_min < tomorrowMinTemp ) tomorrowMinTemp = per3h [i].main.temp_min;
        if ( per3h [i].main.temp_max > tomorrowMaxTemp ) tomorrowMaxTemp = per3h [i].main.temp_max;
    }
}

var messages = {
/*    display: {
        duration: 5
    }, */
    messages: [ 
        { line:2, text: temperatureMessage ( "heute", todayMinTemp, todayMaxTemp ) },
        { line:3, text: descriptionMessage ( "heute", msg.payload [0].weather [0].description ) },
        { line:4, text: temperatureMessage ( "morgen", tomorrowMinTemp, tomorrowMaxTemp ) },
        { line:5, text: descriptionMessage ( "morgen", msg.payload [8].weather [0].description ) }
    ] 
};

msg.payload = messages; // preserve msg attributes!!!

return msg;

