// junand 15.01.2017
// convert open weather forecast to pixel node message structure
//
// the payload is the 5 day forecast from open weather
// msg.payload [0] -> today
// msg.payload [1] -> tomorrow
// ...
// msg.payload [4] -> ...

function temperatureMessage ( day, temp ) {
    return day + " " + temp.night + "°C bis " + temp.day + "°C";
}

function descriptionMessage ( day, description ) {
    return day + " " + description;
}

var messages = {
/*    display: {
        duration: 5
    }, */
    messages: [ 
        { line:2, text: temperatureMessage ( "heute", msg.payload [0].temp ) },
        { line:3, text: descriptionMessage ( "heute", msg.payload [0].weather [0].description ) },
        { line:4, text: temperatureMessage ( "morgen", msg.payload [1].temp ) },
        { line:5, text: descriptionMessage ( "morgen", msg.payload [1].weather [0].description ) }
    ] 
};

msg.payload = messages; // preserve msg attributes!!!

return msg;

