// junand 16.01.2017

const now = +new Date ();
const start = Date.parse ( msg.payload.start );
const diff = now - start;
const oneDay = 1 * 24 * 60 * 60 * 1000; 

var titleMap = [];
titleMap ["Müll"] = 6;
titleMap ["Papier"] = 7;
titleMap ["Gelber Sack"] = 8;
titleMap ["Biotonne"] = 9;

var s = null;
if ( Math.abs ( diff ) < oneDay ) {
    if ( diff < 0 ) {
        s = "morgen";
    }
    else {
        s = "heute";
    }
    s += " ist " + msg.payload.title;
}

var messages;

if ( s === null ) {
    messages = { messages: [{ line: titleMap [msg.payload.title], clear: true } ]};
}
else {
    messages = { messages: [{ line: titleMap [msg.payload.title], text: s } ]};
}

msg.payload = messages; // preserve msg attributes!!!

return msg;

