// reset topics array
if ( msg.topic == "reset" ) {
    flow.set ( "topics", [] );
    return null;
}
else if ( msg.topic == "exception_topics" ) {
    flow.set ( "exception_topics", msg.payload );
    return null;
}

// 1. check for array existence
var topics = flow.get ( "topics" );
if ( topics === undefined ) {
    topics = [];
    flow.set ( "topics", topics );
}

var store_topic_timestamp = true;

// 2. lookup for exception topic
var exceptions = flow.get ( "exception_topics" );
if ( exceptions !== undefined ) {
    for ( i = 0; i < exceptions.length; i++ ) {
        if ( msg.topic == exceptions [i] ) {
            store_topic_timestamp = false;
        }
    }
}

// 3. lookup for topic
var found = topics.length;
for ( i = 0; i < topics.length; i++ ) {
    if ( topics [i].topic == msg.topic) {
        found = i;
    }
}

node.log ( "topic=" + msg.topic );
node.log ( "store=" + store_topic_timestamp );
node.log ( "found=" + found );

// 4. store/delete timestamp
if ( store_topic_timestamp ) {
    topics [found] = { topic: msg.topic, timestamp: msg.timestamp };
}
else if ( found < topics.length ) {
    topics.splice ( found, 1 );
}
flow.set ( "topics", topics );

// 5. return topic array
msg.payload = topics;

return msg;

