//----------------------------------------------------------------
const DEFAULT_RECIPE_NAME = "unknown";
const DEFAULT_BREW_START = "unknown";
const DEFAULT_TEMP = 1;
const DEFAULT_HEATING = "control";
const DEFAULT_HYSTERESIS = 1;
const DEFAULT_DURATION = 1;
const DEFAULT_HEATER_ON = false;

var recipeName = context.get ( "recipeName" );
var brewStart = context.get ( "brewStart" );
var temperature = context.get ( "temperature" ) || DEFAULT_TEMP;
var heatingMode = context.get ( "heating" ) || DEFAULT_HEATING;
var hysteresis = context.get ( "hysteresis" ) || DEFAULT_HYSTERESIS;
var periodLength = context.get ( "periodLength" ) || DEFAULT_DURATION;
var alarm = context.get ( "alarm" );

const PERIOD_INCREMENT = 10;
const PERIOD_ZERO = { min: 0, sec: 0 };
var periodCount = context.get ( "periodCount" ) || PERIOD_ZERO;
var heaterOn = context.get ( "heaterOn" ) || DEFAULT_HEATER_ON;

//----------------------------------------------------------------
const STATE_IDLE = "idle";
const STATE_STARTING = "starting";
const STATE_PERIOD_STARTED = "started";
const STATE_PERIOD_FINISHED = "finished";

var state = context.get ( "state" ) || STATE_IDLE;

//----------------------------------------------------------------
function incPeriodCount ( cnt ) {
    //cnt.sec++;
    cnt.sec += 10;
    if ( cnt.sec > 59 ) {
        cnt.sec = 0;
        cnt.min++;
    }
    return cnt;
}

//----------------------------------------------------------------
function createHeaterMessage ( onOff ) { return { payload: onOff }; }
function createStepMessage () { return { topic: "next", payload: "x" }; }
function createPeriodeMessage ( count ) { return { payload: count.min + ":" + ("0" + count.sec).slice ( -2 ) }; }
function createAlarmMessage ( type ) { return { payload: type }; }

function createChartMessage ( temp, target ) { 
    if ( target ) {
        return [
            { topic: "value", payload: temp },
            { topic: "target", payload: target }
        ];
    }
    else {
        return { topic: "value", payload: temp };
    }
}

function createInfluxMessage ( brewTemp, outerTemp ) {
    return {
        //measurement: "prod",
        payload: [
            // fields
            {
                state: state,
                brewTemp: brewTemp,
                target: temperature,
                heater: heaterOn ? "on" : "off",
                outerTemp: outerTemp
            },
            // tags
            {
                recipe: recipeName,
                brewStart: brewStart
            }
        ]
    };
}

//----------------------------------------------------------------
if ( msg.topic == "stop" ) {
    context.set ( "state", STATE_IDLE );
    node.status ( { fill: "grey", shape: "dot", text: "idle" } );
    return { payload: "OFF" };
}
else if ( msg.topic == "finished" ) {
    context.set ( "state", STATE_PERIOD_FINISHED );
    node.status ( { fill: "grey", shape: "dot", text: "finished" } );
    return { payload: "OFF" };
}
else if ( msg.topic == "start" ) {

    recipeName = msg.recipeName || recipeName || DEFAULT_RECIPE_NAME;
    brewStart = msg.brewStart || brewStart || DEFAULT_BREW_START;
    var t = msg.temperature || DEFAULT_TEMP; 
    if ( t > 0 && t < 105 ) temperature = t;
    heatingMode = msg.heating || heatingMode || DEFAULT_HEATING;
    var h = msg.hysteresis || DEFAULT_HYSTERESIS;
    if ( h > 0 && h < 10 ) hysteresis = h;
    var p = msg.duration || DEFAULT_DURATION;
    if ( p === "wait" || p > 0 ) periodLength = p;

    //node.warn ( "mode=" + heatingMode + " t=" + temperature + " h=" + hysteresis + " p=" + periodLength );

    context.set ( "recipeName", recipeName );
    context.set ( "brewStart", brewStart );
    context.set ( "temperature", temperature );
    context.set ( "heating", heatingMode );
    context.set ( "hysteresis", hysteresis );
    context.set ( "periodLength", periodLength );
    context.set ( "alarm", msg.alarm );

    context.set ( "periodCount", PERIOD_ZERO );
    context.set ( "heaterOn", heaterOn );

    context.set ( "state", STATE_STARTING );
    node.status ( { fill: "grey", shape: "dot", text: "starting" } );

    return [null, null, null, createPeriodeMessage ( PERIOD_ZERO )];

}
else if ( msg.topic == "hysteresis" ) {
    context.set ( "hysteresis", hysteresis );
}
else if ( msg.topic == "tick" ) { // every sec
    // check for period length
    if ( state == STATE_IDLE ) {
        node.status ( { fill: "grey", shape: "dot", text: "idle" } );
    }
    else if ( state == STATE_PERIOD_STARTED ) {
        context.set ( "periodCount", incPeriodCount ( periodCount ) );
        var alarmMsg = null;
        if ( alarm ) {
            for ( var i = 0; i < alarm.length; i++ ) {
                if ( periodCount.min == alarm [i] ) {
                    if ( periodCount.sec === 0 || alarm [i] === 0 && periodCount.sec === PERIOD_INCREMENT ) { // exception for 00:00, 1 sec later
                        alarmMsg = createAlarmMessage ( "ring" );
                        break;
                    }
                }
            }
        }
        if ( periodLength == "wait" ) {} // do nothing, only tick and maybe ring
        else {
            if ( periodCount.min >= periodLength ) {
                context.set ( "state", STATE_PERIOD_FINISHED );
                context.set ( "temperature", null );
                // ??? here heater off ???
                if ( !alarmMsg ) alarmMsg = createAlarmMessage ( "tweet2x" );
                return [createHeaterMessage ( "OFF" ), createStepMessage (), null, createPeriodeMessage ( periodCount ), alarmMsg]; 
            }
        }
        // tick
        return [null, null, null, createPeriodeMessage ( periodCount ), alarmMsg];
    }
}
else if ( msg.topic == "nodes@home/switch/sonoff/brewery/socket" ) {
    context.set ( "heaterOn", msg.payload == "ON" ? true : false );
}
else {
    // temperature received
    if ( state == STATE_IDLE ) {
        node.status ( { fill: "grey", shape: "dot", text: "idle" } );
    }
    else if ( msg.payload && msg.payload.brew && msg.payload.outer ) {
        var brewTemp = msg.payload.brew;
        var outerTemp = msg.payload.outer;
        if ( state == STATE_PERIOD_FINISHED ) {
            return [null, null, createChartMessage ( brewTemp ), null, null, createInfluxMessage ( brewTemp, outerTemp )];
        }
        else {

            var heaterMsg = null;
            var alarmMsg = null;
            var newHeaterOn = null;

            if ( brewTemp >= temperature ) {
                if ( state == STATE_STARTING ) {
                    context.set ( "state", STATE_PERIOD_STARTED );
                    alarmMsg = createAlarmMessage ( "tweet2x" );
                }
            }

            if ( heatingMode == "no" ) {
                if ( heaterOn ) newHeaterOn = false;
            }
            else if ( heatingMode == "one_time" ) {
                if ( state == STATE_STARTING ) {
                    if ( !heaterOn ) newHeaterOn = true;
                }
            }
            else if ( heatingMode == "control" ) {
                if ( brewTemp <= temperature - hysteresis ) {
                    if ( !heaterOn ) newHeaterOn = true;
                }
            }
            else if ( heatingMode == "full" ) {
                if ( !heaterOn ) newHeaterOn = true;
            }

            if ( newHeaterOn !== null ) {
                //node.warn ( "switch heater from " + heaterOn + " to " + newHeaterOn );                    
                context.set ( "heaterOn", newHeaterOn );
                if ( !alarmMsg ) alarmMsg = createAlarmMessage ( "tweet" );
                if ( newHeaterOn ) {
                    node.status ( { fill: "red", shape: "dot", text: "heating" } );
                    heaterMsg = createHeaterMessage ( "ON" );
                }
                else {
                    node.status ( { fill: "blue", shape: "dot", text: "cooling" } );
                    heaterMsg = createHeaterMessage ( "OFF" );
                }
            }

            // on every temp, send target and data fields
            return [heaterMsg, null, createChartMessage ( brewTemp, temperature ), null, alarmMsg, createInfluxMessage ( brewTemp, outerTemp )];
        }
    }
}

