function getStopMessages ( topic ) {

    return [
        { topic: topic||"stop", payload: "x" },
        [
            { id: 0, payload: false },
            { id: 1, payload: false },
            { id: 2, payload: false },
            { id: 3, payload: false },
            { id: 4, payload: false },
            { id: 5, payload: false },
            { id: 6, payload: false },
            { id: 7, payload: false }
        ]
    ];

}

function createDateString ( d ) {

    var datestring = 
        ("0" + d.getDate ()).slice ( -2 ) + "-" + 
        ("0" + (d.getMonth () + 1)).slice ( -2 ) + "-" +
        d.getFullYear () + " " + 
        ("0" + d.getHours ()).slice ( -2 ) + ":" + 
        ("0" + d.getMinutes ()).slice ( -2 );

    return datestring;        
}

function getStepMessages ( i ) {

    var recipe = context.get ( "recipe" );
    if ( i === 0 ) { 
        recipe.brewStart = createDateString ( new Date () );
        context.set ( "recipe", recipe );
    }
    if ( i < recipe.steps.length ) {
        // start controller
        context.set ( "step", i + 1 );
        return [
            {   
                topic: "start", 
                recipeName: recipe.name,
                brewStart: recipe.brewStart,
                temperature: recipe.steps [i].temperature, 
                heating: recipe.steps [i].heating,
                duration: recipe.steps [i].duration, 
                hysteresis: recipe.steps [i].hysteresis,
                alarm: recipe.steps [i].alarm 
            },
            [
                { id: 0, payload: i === 0 ? true : false },
                { id: 1, payload: i === 1 ? true : false },
                { id: 2, payload: i === 2 ? true : false },
                { id: 3, payload: i === 3 ? true : false },
                { id: 4, payload: i === 4 ? true : false },
                { id: 5, payload: i === 5 ? true : false },
                { id: 6, payload: i === 6 ? true : false },
                { id: 7, payload: i === 7 ? true : false }
            ]
        ];
    }
    else {
        // stop controller
        context.set ( "step", null );
        return getStopMessages ( "finished" );
    }

}

if ( msg.topic == "recipe" ) {
    context.set ( "recipe", msg.payload );
    return getStopMessages ();
}
else if ( msg.topic == "start" ) {
    context.set ( "step", 0 );
    return getStepMessages ( 0 );
}
else if ( msg.topic == "stop" ) {
    context.set ( "step", null );
    return getStopMessages ();
}
else if ( msg.topic == "next" ) {
    var step = context.get ( "step" );
    if ( step ) return getStepMessages ( step );
}

