if ( msg.payload.status == "OK" ) {

    s = "ok"

    const leg = msg.payload.routes [0].legs [0];

    s = "Anfahrt Hannover dauert ";
    if ( leg.duration_in_traffic !== undefined ) {
        s += leg.duration_in_traffic.text + " statt ";
    }
    s += leg.duration.text + " für " + leg.distance.text;


}

var messages;

if ( s === null ) {
    messages = { messages: [{ line: 10, clear: true } ]};
}
else {
    messages = { messages: [{ line: 10, text: s } ]};
}

msg.payload = messages; // preserve msg attributes!!!

return msg;

// junand 20.01.2017

function durationText ( secs ) {

    const min = Math.floor ( secs / 60 );
    const hour = Math.floor ( min / 60 );

    var result = "";
    if ( hour > 0 ) result += hour + "h ";
    result += min % 60 + "min"

    return result;

}

if ( msg.payload.status != "OK" ) return null;


var line = 10;
var s = "H: ";
if ( msg.topic == "Undine" ) {
    line = 11;
    s = "Arbeitsweg "
}

const leg = msg.payload.routes [0].legs [0];
if ( leg.duration_in_traffic !== undefined ) {
    s += durationText ( leg.duration_in_traffic.value ) + " statt ";
}
s += durationText ( leg.duration.value );
//    s += " für " + leg.distance.text;

msg.payload = { messages: [{ line: line, text: s } ]};

return msg;
